import React from "react";
import {BrowserRouter as Router, Route, Redirect, Switch} from 'react-router-dom';
import { useForm } from "react-hook-form";
import "./Register.scss";

const Register = props => {
    // const { handleSubmit, register, errors } = useForm();
    const { register, errors, getValues, handleSubmit } = useForm();
    const onSubmit = values => {
        console.log(values);
        //if user found
        if(true) {
            return <Redirect to='/dashboard/'/>
        }
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className="login-container">
                <div className="title-container">
                    <h1>Sign up</h1>
                </div>

                <div className="email-input">
                    <input
                        name="email"
                        ref={register({
                            required: "Required",
                            pattern: {
                                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                                message: "Please enter a valid email address."
                            }
                        })}
                        placeholder="student@uh.edu"
                    />
                </div>
                <div className="email-input">
                    <input
                        name="username"
                        ref={register({
                            required: "Required",
                            max:20
                        })}
                        placeholder="username"
                    />
                </div>

                <div className="email-input">
                <input name="password" type="password" placeholder="••••••••" ref={register({required: 'Password is required!', min: 8, max: 20})}></input>
                </div>

                <div className="email-input">
                    <input name="passwordConfirmation" type="password" placeholder="retype password" ref={register({
                        required: 'Please confirm password!',
                        validate: {
                            matchesPreviousPassword: (value) => {
                            const { password } = getValues();
                            return password === value || 'Passwords should match!';
                            },
                        }
                    })} />
                </div>



                <div className="submit-btn-group">
                    <div>
                        <button type="submit" className="btn btn-register">
                            Sign up
                        </button>
                    </div> 
                </div>
                <div className="error-container">
                    <p>{errors.password && errors.password.message}</p>
                    <p>{errors.passwordConfirmation && errors.passwordConfirmation.message}</p>
                    <p>{errors.email && errors.email.message}</p>
                </div>
            </div>
        </form>
    );
};

export default Register;
