import React from "react";
import { useForm } from "react-hook-form";
import "./Login.scss";

const Login = props => {
    const { handleSubmit, register, errors } = useForm();
    const onSubmit = values => {
        console.log(values);
    };
    return (
        <div className="login-container">
            <div className="title-container">
                <h1>Regsiter</h1>
            </div>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div className="email-input">
                    <input
                        name="email"
                        ref={register({
                            required: "Required",
                            pattern: {
                                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                                message: "Please enter a valid email address."
                            }
                        })}
                        placeholder="student@uh.edu"
                    />
                    <svg viewBox="0 0 18 18">
                        <path d="M11.5,10.5 C6.4987941,17.5909626 1,3.73719105 11.5,6 C10.4594155,14.5485365 17,13.418278 17,9 C17,4.581722 13.418278,1 9,1 C4.581722,1 1,4.581722 1,9 C1,13.418278 4.581722,17 9,17 C13.418278,17 17,13.42 17,9"></path>
                        <polyline points="5 9.25 8 12 13 6"></polyline>
                    </svg>
                </div>
                <div className="email-input">
                    <input
                        type="password"
                        name="password"
                        ref={register({ min: 8, max: 20 })}
                        placeholder="••••••••"
                    />
                    <svg viewBox="0 0 18 18"></svg>
                </div>
                <div className="submit-btn-group">
                    <div>
                        <button type="submit" className="btn btn--1">
                            Register
                        </button>
                    </div>
                    <div>
                        <button className="btn btn--2 btn-forgot">
                            Already have an account?
                        </button>
                    </div>
                </div>
                <div className="error-container">
                    {errors.email && errors.email.message}
                </div>
            </form>
        </div>
    );
};

export default Login;
