import React from "react";
import { useForm } from "react-hook-form";
import "./Register.scss";

const Register = props => {
    const { handleSubmit, register, errors, getValues } = useForm();
    const onSubmit = values => {
        console.log(values);
    };

    // const { register, errors, getValues, handleSubmit } = useForm();
    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className="login-container">
                <div className="title-container">
                    <h1>Sign up</h1>
                </div>

                <div className="email-input">
                    <input
                        name="email"
                        ref={register({
                            required: "Required",
                            pattern: {
                                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                                message: "Please enter a valid email address."
                            }
                        })}
                        placeholder="student@uh.edu"
                    />
                </div>
                <div className="email-input">
                    <input
                        name="username"
                        ref={register({
                            required: "Required",
                            max:20
                        })}
                        placeholder="username"
                    />
                </div>

                {/* <div className="email-input">
                    <input
                        type="password"
                        name="password"
                        ref={register({ min: 8, max: 20 })}
                        placeholder="••••••••"
                    />
                </div> */}

                <input name="password" ref={register({ required: 'Password is required!' })} />
        {errors.password && <p style={{ color: 'white' }}>{errors.password.message}</p>}

        <label>Confirm Password: </label>
        <input name="passwordConfirmation" ref={register({
          required: 'Please confirm password!',
          validate: {
            matchesPreviousPassword: (value) => {
              const { password } = getValues();
              return password === value || 'Passwords should match!';
            },
          }
        })} />
        {errors.passwordConfirmation && <p style={{ color: 'white' }}>{errors.passwordConfirmation.message}</p>}

                <div className="submit-btn-group">
                    <div>
                        <button type="submit" className="btn btn-register">
                            Sign up
                        </button>
                    </div> 
                </div>
                <div className="error-container">
                    {errors.email && errors.email.message}
                </div>
            </div>
        </form>
    );
};

export default Register;
